def binary_search(list, item):    min_index = 0    max_index = len(list) - 1    while min_index <= max_index:        mid_index = (min_index + max_index) // 2        mid_value = list[mid_index]        if item == mid_value:            return mid_value        if item > mid_value:            min_index = mid_index + 1        else:            max_index = mid_index - 1    return Nonemy_list = [1, 3, 5, 7, 9]print(binary_search(my_list, 3))